Select Request_at as Day, cast(sum(case when Status in ('cancelled_by_client', 'cancelled_by_driver') then 1.0 else 0.0 end)/sum(1.0) as decimal(11, 2)) 'Cancellation Rate' from Trips t left join Users u1 on (u1.Users_Id=t.Client_Id AND u1.Banned='No') left join Users u2 on (u2.Users_Id=t.Driver_id AND u2.Banned='No') where u2.Users_Id is not null AND u1.Users_Id is not null and Request_at between '2013-10-01' and '2013-10-03' group by Request_at;